    fun addUser(stuData: StuData): Boolean {
        val list = getUser().toMutableSet()
        if (list.isEmpty()) {
            list.add(stuData)
            saveUserData(gson.toJson(list))
            return true
        }
        if (list.add(stuData)) {
            saveUserData(gson.toJson(list))
            return true
        }
        return false
    }

    private fun saveUserData(str: String = "") {
        File(formPath).writeText(str)
    }

    fun delUser(stuData: StuData) {
        val data = getUser()
        val list = mutableListOf<StuData>()
        for (i in data) {
            if (i.name != stuData.name) {
                list.add(i)
            }
        }
        saveUserData(gson.toJson(list))
    }

    fun getUser(): MutableList<StuData> {
        val array = mutableListOf<StuData>()
        val a: JsonArray = JsonParser.parseReader(File(formPath).reader()).asJsonArray
        for (i in a) {
            array.add(gson.fromJson(i, StuData::class.java))
        }
        return array
    }

    suspend fun loopGetCookie() = flow {
        val data = getUser()
        val list = mutableListOf<XXTResult>()
        for (i in data) {
            //1.http
            val httpClient = HttpClient {
                install(HttpCookies)
                install(ContentNegotiation)
            }
            //2.Cookie
            getCookie(phone = i.phoneNumber, pwd = i.pwd, httpClient = httpClient)

            //3.Cookie UIDCheckCode
            val l = getCookie2(httpClient = httpClient)
            val checkCode = l[0]
            val uid = l[1]
            //4.

            list.add(
                check(
                    httpClient = httpClient,
                    name = i.name,
                    phone = i.phoneNumber,
                    id = i.id,
                    uid = uid,
                    checkCode = checkCode,
                    xueyuan = i.xueyuan,
                    zhuanye = i.zhuanye,
                    banji = i.banji
                )
            )
        }

        emit(list)
    }.distinctUntilChanged()

    /**
     * Cookies
     * @param url 
     * @param phone 
     * @param pwd 
     */
    private suspend fun getCookie(
        phone: String,
        pwd: String,
        httpClient: HttpClient,
        url: String = "https://passport2.chaoxing.com/fanyalogin",
    ) {

        val encryptPwd = with(StringBuilder()) {
            for (i in DesCrypt.encrypt("u2oh6Vu^HWe40fj", pwd)) {
                append(String.format("%02x", i))
            }
            toString()
        }
        val bodyData =
            "fid=-1&" +
                    "pid=-1&" +
                    "refer=https://i.mooc.chaoxing.com&" +
                    "t=true&" +
                    "forbidotherlogin=0&" +
                    "validate=&" +
                    "uname=$phone&" +
                    "password=${encryptPwd}"
        httpClient.request(url) {
            method = HttpMethod.Post
            setBody(bodyData)
            headers {
                append("User-Agent", "client")
                append("Content-Type", "application/x-www-form-urlencoded")
            }
        }


    }

    private suspend fun getCookie2(httpClient: HttpClient): List<String> {
        val ge = httpClient.request(
            "https://office.chaoxing.com/front/web/apps/forms/fore/apply?id=89398&" +
                    "formAppId=&enc=75244f75384287c902e57b080c4d1c6d&appId=225643a2aace4d338d2138ff02a91d35&" +
                    "appKey=Ii6%2F21Qwhlellv7t&uid=192200477&mappId=5956296&from_type=space&fidEnc=9af49f98caedfb44&code=Ossscuqd" +
                    "&state=170118"
        ) {
            method = HttpMethod.Get
        }
        val bodyText = ge.bodyAsText()
        val prefix =
            bodyText.substring(bodyText.indexOf("checkCode = '") + "checkCode = '".length)
        var uid = ""
        for (j in httpClient.cookies("https://passport2.chaoxing.com/fanyalogin")) {
            if (j.name == "_uid") {
                uid = j.value
            }
        }
        return listOf(prefix.substring(0, prefix.indexOf("'")), uid)
    }

