        phone: String,
        id: String,
        uid: String,
        checkCode: String,
        xueyuan: String,
        zhuanye: String,
        banji: String
    ): XXTResult {
        val o =
            httpClient.request("https://office.chaoxing.com/data/apps/forms/fore/user/save") {
                method = HttpMethod.Post
                headers {
                    append(
                        "User-Agent",
                        "Mozilla/5.0 (Linux; Android 12;) AppleWebKit/537.36 (KHTML, like Gecko) Version/4.0 Chrome/101.0.4951.41 Mobile Safari/537.36 Language/zh_CN com.chaoxing.mobile/ChaoXingStudy_3_5.1.4_android_phone_614_74 (@Kalimdor)_482bfb22af77461b96e77e64aa40abc2"
                    )
                    append("Host", "office.chaoxing.com")
                    append("Connection", "keep-alive")
                    append("Accept", "application/json, text/javascript, */*; q=0.01")
                    append("X-Requested-With", "XMLHttpRequest")
                    append("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8")
                    append("Origin", "https://office.chaoxing.com")
                    append("Sec-Fetch-Site", "same-origin")
                    append("Sec-Fetch-Mode", "cors")
                    append("Sec-Fetch-Dest", "empty")
                    append(
                        "Referer",
                        "https://office.chaoxing.com/front/apps/forms/fore/apply?id=89398&formAppId=&enc=75244f75384287c902e57b080c4d1c6d&appId=225643a2aace4d338d2138ff02a91d35&appKey=Ii6%2F21Qwhlellv7t&fidEnc=9af49f98caedfb44&uid=$uid&mappId=5956296&code=&state=170118"
                    )
                    append("Accept-Encoding", "gzip, deflate")
                    append("Accept-Language", "zh-CN,zh;q=0.9,en-US;q=0.8,en;q=0.7")
                }
                setBody(
