    private fun convertForm(
        name: String,
        uid: String,
        phone: String,
        id: String,
        xueyuan: String,
        zhuanye: String,
        banji: String
    ): String {
        val data = File(resPath).readText().decodeURLPart()
        val formData = gson.fromJson(data, FormData::class.java)
        val simple = SimpleDateFormat("yyyy-MM-dd", Locale.CHINA)
        val date = Calendar.getInstance().time
        for (i in formData) {
            if (i.alias == 38) {
                i.fields[0].values[0]["val"] = simple.format(date)
            }
        }
        formData[0].fields[0].values[0]["puid"] = uid
        formData[0].fields[0].values[0]["uname"] = name
        formData[4].fields[0].values[0]["val"] = id
        formData[5].fields[0].values[0]["val"] = phone
        formData[13].fields[0].values[0]["val"] = ""
        formData[1].fields[0].values[0]["val"] = xueyuan
        formData[2].fields[0].values[0]["val"] = zhuanye
        formData[3].fields[0].values[0]["val"] = banji

        formData[14].fields[0].values = listOf(mutableMapOf("val" to simple.format(date)))
        formData[15].fields[0].values = listOf(mutableMapOf("val" to ""))
        return gson.toJson(formData).encodeURLPath()
    }

